def save(a=None):
    global saved
    global fpath
    global code_
    if saved == 0:
        if a is None:
            if askyesno("Brainfuck IDE", "Do you want to save the file?"):
                temp = filesave(defaultextension='.bf', filetypes=[("Brainfuck Files", "*.bf"), ("Text files", "*.txt")])
                if temp is not None:
                    f = open(temp.name, "w")
                    fpath = temp.name
                    f.write(codeinp.get("1.0", END)[:-1])
                    f.close()
                    saved = 1
                    win.title("Brainfuck IDE: " + fpath)
        else:
            temp = filesave(defaultextension='.bf', filetypes=[("Brainfuck Files", "*.bf"), ("Text files", "*.txt")])
            if temp is not None:
                f = open(temp.name, "w")
                fpath = temp.name
                f.write(codeinp.get("1.0", END)[:-1])
                f.close()
                saved = 1
                win.title("Brainfuck IDE: " + fpath)
    else:
        f = open(fpath, "w")
        f.write(codeinp.get("1.0", END)[:-1])
        code_ = codeinp.get("1.0", END)
        f.close()
        win.title("Brainfuck IDE: " + fpath)
    code_ = codeinp.get("1.0", END)
    check()
    return
code_ = "\n"
def open_(a=None):
    global fpath, saved, code, code_
    if codeinp.get("1.0", END) != "\n":
        save()
    temp = askopenfile(defaultextension='.bf', filetypes=[("Brainfuck Files", "*.bf"), ("Text files", "*.txt"), ("All files", "*.*")])
    if temp is not None:
        f = open(temp.name, "r")
        fpath = temp.name
        codeinp.delete("1.0", END)
        codeinp.insert(INSERT, f.read())
        code = f.read()
        code_ = f.read()
        f.close()
        saved = 1
    save()
    win.title("Brainfuck IDE: " + fpath)
    check()
run = 0
def evaluate(z=None):
    global run
    print_("Program running.\n")
    if codeinp.get("1.0", END) != "\n":
        try:
            code = codeinp.get("1.0", END)
            run = 1
            code = cleanup(list(code))
            bracemap = []
            cells, codeptr, cellptr = [0], 0, 0
            while codeptr < len(code):
                if run == 1:
                    command = code[codeptr]
                    if command == ">":
                        cellptr += 1
                    if cellptr == len(cells): cells.append(0)
                    if command == "<":
                        cellptr = 0 if cellptr <= 0 else cellptr - 1
                    if command == "+":
                        cells[cellptr] += 1
                        cells[cellptr] %= 256

                    if command == "-":
                        cells[cellptr] -= 1
                        cells[cellptr] %= 256
                    if command == "[":
                        bracemap.append(codeptr)
                    if command == "]":
                        if cells[cellptr] == 0:
                            bracemap.pop()
                        else:
                            codeptr = bracemap[-1]
                    if command == ".": print_(chr(cells[cellptr]))
                    if command == ",": cells[cellptr] = ord(input())
                    codeptr += 1
                    win.update()
                else:
                    print_("\nProgram halted.\n")
                    return
            print_("\nProgram finished.\n")
        except Exception as e:
            stop()
            print_("\nAn exception has occurred. Details: " + str(e) + "\n")
            return
        stop()
